<?php

namespace SMXD\Media\Controllers\API;

use SMXD\Application\Lib\SMXDLetterImage;
use \SMXD\Media\Controllers\ModuleApiController;
use SMXD\Media\Models\Media;

/**
 * Concrete implementation of Media module controller
 *
 * @RoutePrefix("/media/api")
 */
class IndexController extends ModuleApiController
{
    /**
     * @Route("/index", paths={module="media"}, methods={"GET"}, name="media-index-index")
     */
    public function indexAction()
    {
        die(__FUNCTION__);
    }

    /**
     * block all access
     * @return [type]
     */
    public function blockAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->setJsonContent(['success' => false, 'message' => 'VIEW_PERMISSION_NOT_FOUND_TEXT']);
        $this->response->send();
    }

    /**
     * block all access
     * @return [type]
     */
    public function expireAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->setJsonContent(['success' => false, 'message' => 'TOKEN_EXPIRED_TEXT']);
        $this->response->send();
    }

    /**
     * block all access
     * @return [type]
     */
    public function failAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->setJsonContent(['success' => false, 'message' => 'HOSTING_FAILED_TEXT']);
        $this->response->send();
    }

    /**
     * block all access
     * @return [type]
     */
    public function failHtmlAction()
    {
        $this->view->enable();
        $this->view->setLayout("error");
        $this->view->pick('index/error');
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->send();
    }

    /**
     * block all access
     * @return [type]
     */
    public function failImageAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->setContentType(Media::MIME_TYPE_PNG);
        $this->response->setContent(base64_decode(""));
        return $this->response->send();
    }

    /**
     * @return \Phalcon\Http\Response|\Phalcon\Http\ResponseInterface
     */
    public function notFoundWithMessageAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->setContentType(Media::MIME_TYPE_PNG);
        $this->response->setContent(base64_decode(""));
        return $this->response->send();
    }

    /**
     * @return \Phalcon\Http\Response|\Phalcon\Http\ResponseInterface
     */
    public function notFoundAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->setContentType(Media::MIME_TYPE_PNG);
        $this->response->setContent(base64_decode("
        "));
        return $this->response->send();
    }

    /**
     * @return \Phalcon\Http\Response|\Phalcon\Http\ResponseInterface
     */
    public function thumbNotFoundAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->setContentType(Media::MIME_TYPE_PNG);
        $this->response->setContent(base64_decode(""));
        return $this->response->send();
    }

    /**
     *
     */
    public function thumbExpiredAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $avatar = new SMXDLetterImage('TOKEN EXPIRED', 'rectangle', 128, 'E5F4F3', 3);
        $content = $avatar->__toString();
        $this->response->setContentType(Media::MIME_TYPE_PNG);
        $this->response->setContent(base64_decode(explode(',', $content)[1]));
        return $this->response->send();
    }

    /**
     * block all access
     * @return [type]
     */
    public function permissionNotFoundAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $this->response->setContentType(Media::MIME_TYPE_PNG);
        $this->response->setContent(base64_decode(""));
        return $this->response->send();
    }

    /**
     * block all access
     * @return [type]
     */
    public function failImageRectangleAction()
    {
        $this->view->disable();
        $this->response->setStatusCode(404, "Not Found Content");
        $avatar = new SMXDLetterImage('4 0 4', 'rectangle', 128, 'E5F4F3', 3);
        $content = $avatar->__toString();
        $this->response->setContentType(Media::MIME_TYPE_PNG);
        $this->response->setContent(base64_decode(explode(',', $content)[1]));
        return $this->response->send();
    }

    /**
     * Maintenance page
     */
    public function maintenanceAction()
    {
        $this->view->enable();
        $this->view->setLayout("error");
        $this->view->pick('index/maintenance');
        $this->response->setStatusCode(403, "Forbidden");
        $this->response->send();
    }
}
